import React, { useState } from 'react';
import "./header.css";

export default function Header() {

    const [Toggle, showMenu] = useState(false);

    const NavItem = (props) => {

        let iconClass = `uil uil-${props.icon} nav_icon`;

        return (
            <li className="nav_item">
                <a href={props.href} className='nav_link active-link'>
                    <i className={iconClass}></i>
                    {props.iconName}
                </a>

            </li>
        )
    }

    return (
        <header className="header">
            <nav className="nav container">
                <a href="index.html" className="nav_logo">Richard</a>

                <div className="nav_menu">
                    <ul className="nav_list grid">
                        <NavItem href='#home' icon='estate' iconName='Home'/>
                        <NavItem href='#about' icon='user' iconName='About'/>
                        <NavItem href='#skills' icon='file-alt' iconName='Skills'/>
                        <NavItem href='#services' icon='briefcase-alt' iconName='Services'/>
                        <NavItem href='#portfolio' icon='scenery' iconName='Portfolio'/>
                        <NavItem href='#contact' icon='message' iconName='Contact'/>

                        <i class="uil uil-times nav_close"></i>
                    </ul>
                </div>

                <div className="nav_toggle">
                    <i class='uil uil-apps'></i>
                </div>
            </nav>
        </header>
    )
}
